   138‚Üífunction formatTodoList(todos: any[]): string {
   139‚Üí  let output = `üìã ${colors.bright}${colors.cyan}Todo List Update${colors.reset}\n`;
   140‚Üí
   141‚Üí  const statusColors = {
   142‚Üí    completed: colors.dim + colors.green,
   143‚Üí    in_progress: colors.bright + colors.yellow,
   144‚Üí    pending: colors.reset,
   145‚Üí  };
   146‚Üí
   147‚Üí  const statusIcons = {
   148‚Üí    completed: '‚úÖ',
   149‚Üí    in_progress: 'üîÑ',
   150‚Üí    pending: '‚è∏Ô∏è',
   151‚Üí  };
   152‚Üí
   153‚Üí  const priorityColors = {
   154‚Üí    high: colors.red,
   155‚Üí    medium: colors.yellow,
   156‚Üí    low: colors.dim,
   157‚Üí  };
   158‚Üí
   159‚Üí  todos.forEach((todo, index) => {
   160‚Üí    const statusColor = statusColors[todo.status] || colors.reset;
   161‚Üí    const statusIcon = statusIcons[todo.status] || '‚ùì';
   162‚Üí    const priorityColor = priorityColors[todo.priority] || colors.reset;
   163‚Üí    const checkbox = todo.status === 'completed' ? '‚òëÔ∏è' : '‚òê';
   164‚Üí
   165‚Üí    output += `  ${checkbox} ${statusIcon} ${statusColor}${todo.content}${colors.reset}`;
   166‚Üí    output += ` ${priorityColor}[${todo.priority}]${colors.reset}`;
   167‚Üí
   168‚Üí    if (todo.status === 'in_progress') {
   169‚Üí      output += ` ${colors.bright}${colors.yellow}‚Üê ACTIVE${colors.reset}`;
   170‚Üí    }
   171‚Üí
   172‚Üí    output += '\n';
   173‚Üí  });
   174‚Üí
   175‚Üí  // Add summary stats
   176‚Üí  const completed = todos.filter((t) => t.status === 'completed').length;
   177‚Üí  const inProgress = todos.filter((t) => t.status === 'in_progress').length;
   178‚Üí  const pending = todos.filter((t) => t.status === 'pending').length;
   179‚Üí
   180‚Üí  output += `\n  ${colors.dim}üìä Progress: ${colors.green}${completed} completed${colors.reset}`;
   181‚Üí  output += `${colors.dim}, ${colors.yellow}${inProgress} active${colors.reset}`;
   182‚Üí  output += `${colors.dim}, ${colors.reset}${pending} pending${colors.reset}`;
   183‚Üí  output += `${colors.dim} (${Math.round((completed / todos.length) * 100)}% done)${colors.reset}`;
   184‚Üí
   185‚Üí  return output;
   186‚Üí}
   187‚Üí
   188‚Üífunction formatConcise(json: any): string {
   189‚Üí  const type = json.type || 'unknown';
   190‚Üí  const typeColor = getTypeColor(type);
   191‚Üí
   192‚Üí  let output = `‚è∫ ${typeColor}${type.charAt(0).toUpperCase() + type.slice(1)}${colors.reset}`;
   193‚Üí
   194‚Üí  // Special handling for TodoWrite calls
   195‚Üí  if (type === 'assistant' && json.message?.content?.[0]?.name === 'TodoWrite') {
   196‚Üí    const toolInput = json.message.content[0].input;
   197‚Üí    if (toolInput?.todos && Array.isArray(toolInput.todos)) {
   198‚Üí      return formatTodoList(toolInput.todos);
   199‚Üí    }
   200‚Üí  }
   201‚Üí
   202‚Üí  // Add context based on type
   203‚Üí  if (type === 'assistant' && json.message?.content?.[0]?.name) {
   204‚Üí    const toolName = json.message.content[0].name;
   205‚Üí    const toolInput = json.message.content[0].input;
   206‚Üí
   207‚Üí    // Format tool name with key arguments
   208‚Üí    let toolDisplay = `${colors.cyan}${toolName}${colors.reset}`;
   209‚Üí
   210‚Üí    if (toolInput) {
   211‚Üí      const keyArgs = [];
   212‚Üí
   213‚Üí      // Extract the most important argument for each tool type
   214‚Üí      if (toolInput.file_path) keyArgs.push(toolInput.file_path);
   215‚Üí      else if (toolInput.path) keyArgs.push(toolInput.path);
   216‚Üí      else if (toolInput.pattern) keyArgs.push(`"${toolInput.pattern}"`);
   217‚Üí      else if (toolInput.command) keyArgs.push(toolInput.command);
   218‚Üí      else if (toolInput.cmd) keyArgs.push(toolInput.cmd);
   219‚Üí      else if (toolInput.query) keyArgs.push(`"${toolInput.query}"`);
   220‚Üí      else if (toolInput.description) keyArgs.push(toolInput.description);
   221‚Üí      else if (toolInput.prompt) keyArgs.push(`"${toolInput.prompt.substring(0, 30)}..."`);
   222‚Üí      else if (toolInput.url) keyArgs.push(toolInput.url);
   223‚Üí
   224‚Üí      if (keyArgs.length > 0) {
   225‚Üí        toolDisplay += `(${colors.green}${keyArgs[0]}${colors.reset})`;
   226‚Üí      }
   227‚Üí    }
   228‚Üí
   229‚Üí    output = `‚è∫ ${toolDisplay}`;
   230‚Üí
   231‚Üí    // Show additional arguments on next lines for complex tools
   232‚Üí    if (toolInput) {
   233‚Üí      const additionalArgs = [];
   234‚Üí
   235‚Üí      if (toolName === 'Bash' && toolInput.cwd) {
   236‚Üí        additionalArgs.push(`cwd: ${toolInput.cwd}`);
   237‚Üí      }
   238‚Üí      if (toolInput.limit) additionalArgs.push(`limit: ${toolInput.limit}`);
   239‚Üí      if (toolInput.offset) additionalArgs.push(`offset: ${toolInput.offset}`);
   240‚Üí      if (toolInput.include) additionalArgs.push(`include: ${toolInput.include}`);
   241‚Üí      if (toolInput.old_string && toolInput.new_string) {
   242‚Üí        additionalArgs.push(
   243‚Üí          `replace: "${toolInput.old_string.substring(0, 20)}..." ‚Üí "${toolInput.new_string.substring(0, 20)}..."`
   244‚Üí        );
   245‚Üí      }
   246‚Üí      if (toolInput.timeout) additionalArgs.push(`timeout: ${toolInput.timeout}ms`);
   247‚Üí
   248‚Üí      if (additionalArgs.length > 0) {
   249‚Üí        output += `\n  ‚éø  ${colors.dim}${additionalArgs.join(', ')}${colors.reset}`;
   250‚Üí      }
   251‚Üí    }
   252‚Üí  } else if (type === 'tool_result' && json.name) {
   253‚Üí    output += `(${colors.cyan}${json.name}${colors.reset})`;
   254‚Üí  } else if (type === 'user' && json.message?.content?.[0]) {
   255‚Üí    const content = json.message.content[0];
   256‚Üí    if (content.type === 'tool_result') {
   257‚Üí      // Override the type display for tool results
   258‚Üí      output = `‚è∫ ${colors.yellow}Tool Result${colors.reset}`;
   259‚Üí
   260‚Üí      // Show result summary and first 2 lines
   261‚Üí      if (content.content) {
   262‚Üí        const resultText =
   263‚Üí          typeof content.content === 'string' ? content.content : JSON.stringify(content.content);
   264‚Üí        const lines = resultText.split('\n');
   265‚Üí        const chars = resultText.length;
   266‚Üí        output += `\n  ‚éø  ${colors.dim}${lines.length} lines, ${chars} chars${colors.reset}`;
   267‚Üí        if (content.is_error) {
   268‚Üí          output += ` ${colors.red}ERROR${colors.reset}`;
   269‚Üí        }
   270‚Üí
   271‚Üí        // Show first 2 lines of content
   272‚Üí        if (lines.length > 0 && lines[0].trim()) {
   273‚Üí          output += `\n  ‚éø  ${colors.reset}${lines[0]}${colors.reset}`;
   274‚Üí        }
   275‚Üí        if (lines.length > 1 && lines[1].trim()) {
   276‚Üí          output += `\n      ${colors.dim}${lines[1]}${colors.reset}`;
   277‚Üí        }
   278‚Üí      }
   279‚Üí    } else if (content.text) {
   280‚Üí      const text = content.text.substring(0, 50);
   281‚Üí      output += `: ${colors.dim}${text}${text.length === 50 ? '...' : ''}${colors.reset}`;
   282‚Üí    }
   283‚Üí  } else if (type === 'system' && json.subtype) {
   284‚Üí    output += `(${colors.dim}${json.subtype}${colors.reset})`;
   285‚Üí  }
   286‚Üí
   287‚Üí  // Show assistant message content if it exists
   288‚Üí  if (type === 'assistant' && json.message?.content) {
   289‚Üí    const textContent = json.message.content.find((c) => c.type === 'text');
   290‚Üí    if (textContent?.text) {
   291‚Üí      const lines = textContent.text.split('\n').slice(0, 3); // Show first 3 lines
   292‚Üí      output += `\n  ‚éø  ${colors.reset}${lines[0]}${colors.reset}`;
   293‚Üí      if (lines.length > 1) {
   294‚Üí        output += `\n      ${colors.dim}${lines[1]}${colors.reset}`;
   295‚Üí      }
   296‚Üí      if (lines.length > 2) {
   297‚Üí        output += `\n      ${colors.dim}${lines[2]}${colors.reset}`;
   298‚Üí      }
   299‚Üí      if (textContent.text.split('\n').length > 3) {
   300‚Üí        output += `\n      ${colors.dim}...${colors.reset}`;
   301‚Üí      }
   302‚Üí    }
   303‚Üí  }
   304‚Üí
   305‚Üí  // Add summary line
   306‚Üí  let summary = '';
   307‚Üí  if (json.message?.usage) {
   308‚Üí    const usage = json.message.usage;
   309‚Üí    summary = `${usage.input_tokens || 0}/${usage.output_tokens || 0} tokens`;
   310‚Üí  } else if (json.output && typeof json.output === 'string') {
   311‚Üí    summary = `${json.output.length} chars output`;
   312‚Üí  } else if (json.message?.content?.length) {
   313‚Üí    summary = `${json.message.content.length} content items`;
   314‚Üí  } else if (json.tools?.length) {
   315‚Üí    summary = `${json.tools.length} tools available`;
   316‚Üí  }
   317‚Üí
   318‚Üí  if (summary) {
   319‚Üí    output += `\n  ‚éø  ${colors.dim}${summary}${colors.reset}`;
   320‚Üí  }
   321‚Üí
   322‚Üí  return output;
   323‚Üí}
   324‚Üí
   325‚Üíasync function processStream() {
   326‚Üí  const rl = createInterface({
   327‚Üí    input: process.stdin,
   328‚Üí    crlfDelay: Infinity,
   329‚Üí  });
   330‚Üí
   331‚Üí  const debugMode = process.argv.includes('--debug');
   332‚Üí  const toolCalls = new Map(); // Store tool calls by their ID
   333‚Üí  const pendingResults = new Map(); // Store results waiting for their tool calls
   334‚Üí  let lastLine = null; // Track the last line to detect final message
   335‚Üí  let isLastAssistantMessage = false;
   336‚Üí
   337‚Üí  rl.on('line', (line) => {
   338‚Üí    if (line.trim()) {
   339‚Üí      const timestamp = debugMode
   340‚Üí        ? `${colors.dim}[${new Date().toISOString()}]${colors.reset} `
   341‚Üí        : '';
   342‚Üí
   343‚Üí      try {
   344‚Üí        const json = JSON.parse(line);
   345‚Üí
   346‚Üí        // Check if this is a tool call
   347‚Üí        if (json.type === 'assistant' && json.message?.content?.[0]?.id) {
   348‚Üí          const toolCall = json.message.content[0];
   349‚Üí          const toolId = toolCall.id;
   350‚Üí
   351‚Üí          // Store the tool call
   352‚Üí          toolCalls.set(toolId, {
   353‚Üí            toolCall: json,
   354‚Üí            timestamp: timestamp,
   355‚Üí          });
   356‚Üí
   357‚Üí          // Check if we have a pending result for this tool call
   358‚Üí          if (pendingResults.has(toolId)) {
   359‚Üí            const result = pendingResults.get(toolId);
   360‚Üí            displayToolCallWithResult(
   361‚Üí              toolCall,
   362‚Üí              json,
   363‚Üí              result.toolResult,
   364‚Üí              result.timestamp,
   365‚Üí              timestamp
   366‚Üí            );
   367‚Üí            pendingResults.delete(toolId);
   368‚Üí          } else {
   369‚Üí            // Display the tool call and mark it as pending
   370‚Üí            process.stdout.write(`${timestamp + formatConcise(json)}\n`);
   371‚Üí            process.stdout.write(`${colors.dim}  ‚éø  Waiting for result...${colors.reset}\n\n`);
   372‚Üí          }
   373‚Üí        }
   374‚Üí        // Check if this is a tool result
   375‚Üí        else if (json.type === 'user' && json.message?.content?.[0]?.type === 'tool_result') {
   376‚Üí          const toolResult = json.message.content[0];
   377‚Üí          const toolId = toolResult.tool_use_id;
   378‚Üí
   379‚Üí          if (toolCalls.has(toolId)) {
   380‚Üí            // We have the matching tool call, display them together
   381‚Üí            const stored = toolCalls.get(toolId);
   382‚Üí            displayToolCallWithResult(
   383‚Üí              stored.toolCall.message.content[0],
   384‚Üí              stored.toolCall,
   385‚Üí              json,
   386‚Üí              stored.timestamp,
   387‚Üí              timestamp
   388‚Üí            );
   389‚Üí            toolCalls.delete(toolId);
   390‚Üí          } else {
   391‚Üí            // Store the result and wait for the tool call
   392‚Üí            pendingResults.set(toolId, {
   393‚Üí              toolResult: json,
   394‚Üí              timestamp: timestamp,
   395‚Üí            });
   396‚Üí          }
   397‚Üí        }
   398‚Üí        // Check if this is the result message and display full content
   399‚Üí        else if (json.type === 'result' && json.result) {
   400‚Üí          process.stdout.write(`${timestamp + formatConcise(json)}\n\n`);
   401‚Üí          process.stdout.write(`${colors.bright}${colors.green}=== Final Result ===${colors.reset}\n\n`);
   402‚Üí          process.stdout.write(`${json.result}\n`);
   403‚Üí        }
   404‚Üí        // For all other message types, display normally
   405‚Üí        else {
   406‚Üí          process.stdout.write(`${timestamp + formatConcise(json)}\n\n`);
   407‚Üí        }
   408‚Üí
   409‚Üí        // Track if this might be the last assistant message
   410‚Üí        lastLine = json;
   411‚Üí        isLastAssistantMessage = json.type === 'assistant' && !json.message?.content?.[0]?.id;
   412‚Üí      } catch (_error) {
   413‚Üí        process.stdout.write(`${timestamp}${colors.red}‚è∫ Parse Error${colors.reset}\n`);
   414‚Üí        process.stdout.write(`  ‚éø  ${colors.dim}${line.substring(0, 50)}...${colors.reset}\n\n`);
   415‚Üí      }
   416‚Üí    }
   417‚Üí  });
   418‚Üí
   419‚Üí  rl.on('close', () => {
   420‚Üí    // If the last message was an assistant message (not a tool call), display the full content
   421‚Üí    if (isLastAssistantMessage && lastLine?.message?.content?.[0]?.text) {
