    16‚Üí
    17‚Üífunction getTypeColor(type: string): string {
    18‚Üí  switch (type) {
    19‚Üí    case 'system':
    20‚Üí      return colors.magenta;
    21‚Üí    case 'user':
    22‚Üí      return colors.blue;
    23‚Üí    case 'assistant':
    24‚Üí      return colors.green;
    25‚Üí    case 'tool_use':
    26‚Üí      return colors.cyan;
    27‚Üí    case 'tool_result':
    28‚Üí      return colors.yellow;
    29‚Üí    case 'message':
    30‚Üí      return colors.dim;
    31‚Üí    case 'text':
    32‚Üí      return colors.reset;
    33‚Üí    default:
    34‚Üí      return colors.reset;
    35‚Üí  }
    36‚Üí}
    37‚Üí
    38‚Üífunction _formatHeader(json: any, lineNumber: number): string {
    39‚Üí  const type = json.type || 'unknown';
    40‚Üí  const typeColor = getTypeColor(type);
    41‚Üí
    42‚Üí  let header = `${colors.dim}--- Line ${lineNumber} ${typeColor}[${type.toUpperCase()}]${colors.reset}`;
    43‚Üí
    44‚Üí  // Add context based on type
    45‚Üí  if (json.message?.role) {
    46‚Üí    header += ` ${colors.dim}(${json.message.role})${colors.reset}`;
    47‚Üí  }
    48‚Üí
    49‚Üí  if (json.message?.content?.[0]?.name) {
    50‚Üí    header += ` ${colors.cyan}${json.message.content[0].name}${colors.reset}`;
    51‚Üí  }
    52‚Üí
    53‚Üí  if (json.name) {
    54‚Üí    header += ` ${colors.cyan}${json.name}${colors.reset}`;
    55‚Üí  }
    56‚Üí
    57‚Üí  if (json.subtype) {
    58‚Üí    header += ` ${colors.dim}${json.subtype}${colors.reset}`;
    59‚Üí  }
    60‚Üí
    61‚Üí  return `${header} ${colors.dim}---${colors.reset}`;
    62‚Üí}
    63‚Üí
    64‚Üífunction _colorizeJson(obj: any, indent = 0, path: string[] = []): string {
    65‚Üí  const spaces = '  '.repeat(indent);
    66‚Üí
    67‚Üí  if (obj === null) return `${colors.dim}null${colors.reset}`;
    68‚Üí  if (typeof obj === 'boolean') return `${colors.yellow}${obj}${colors.reset}`;
    69‚Üí  if (typeof obj === 'number') return `${colors.cyan}${obj}${colors.reset}`;
    70‚Üí  if (typeof obj === 'string') {
    71‚Üí    // Truncate very long strings
    72‚Üí    if (obj.length > 200) {
    73‚Üí      return `${colors.green}"${obj.substring(0, 197)}..."${colors.reset}`;
    74‚Üí    }
    75‚Üí    return `${colors.green}"${obj}"${colors.reset}`;
    76‚Üí  }
    77‚Üí
    78‚Üí  if (Array.isArray(obj)) {
    79‚Üí    if (obj.length === 0) return '[]';
    80‚Üí
    81‚Üí    // For content arrays, show summary
    82‚Üí    if (path.includes('content') && obj.length > 3) {
    83‚Üí      const summary = obj.slice(0, 2).map((item) => _colorizeJson(item, indent + 1, [...path]));
    84‚Üí      return `[\n${summary.join(',\n')},\n${spaces}  ${colors.dim}... ${obj.length - 2} more items${colors.reset}\n${spaces}]`;
    85‚Üí    }
    86‚Üí
    87‚Üí    const items = obj.map((item) => `${spaces}  ${_colorizeJson(item, indent + 1, [...path])}`);
    88‚Üí    return `[\n${items.join(',\n')}\n${spaces}]`;
    89‚Üí  }
    90‚Üí
    91‚Üí  if (typeof obj === 'object') {
    92‚Üí    const keys = Object.keys(obj);
    93‚Üí    if (keys.length === 0) return '{}';
    94‚Üí
    95‚Üí    // Show only key fields for deeply nested objects
    96‚Üí    const importantKeys = [
    97‚Üí      'type',
    98‚Üí      'role',
    99‚Üí      'name',
   100‚Üí      'id',
   101‚Üí      'input',
   102‚Üí      'output',
   103‚Üí      'content',
   104‚Üí      'text',
   105‚Üí      'subtype',
   106‚Üí      'session_id',
   107‚Üí    ];
   108‚Üí    const keysToShow = indent > 2 ? keys.filter((k) => importantKeys.includes(k)) : keys;
   109‚Üí
   110‚Üí    if (keysToShow.length === 0 && keys.length > 0) {
   111‚Üí      return `${colors.dim}{...${keys.length} keys}${colors.reset}`;
   112‚Üí    }
   113‚Üí
   114‚Üí    const items = keysToShow.map((key) => {
   115‚Üí      let coloredKey = `${colors.blue}"${key}"${colors.reset}`;
   116‚Üí
   117‚Üí      // Highlight important keys
   118‚Üí      if (['type', 'name', 'role'].includes(key)) {
   119‚Üí        coloredKey = `${colors.bright}${colors.blue}"${key}"${colors.reset}`;
   120‚Üí      }
   121‚Üí
   122‚Üí      const value = _colorizeJson(obj[key], indent + 1, [...path, key]);
   123‚Üí      return `${spaces}  ${coloredKey}: ${value}`;
   124‚Üí    });
   125‚Üí
   126‚Üí    if (keysToShow.length < keys.length) {
   127‚Üí      items.push(
   128‚Üí        `${spaces}  ${colors.dim}... ${keys.length - keysToShow.length} more keys${colors.reset}`
   129‚Üí      );
   130‚Üí    }
   131‚Üí
   132‚Üí    return `{\n${items.join(',\n')}\n${spaces}}`;
   133‚Üí  }
   134‚Üí
   135‚Üí  return String(obj);
   136‚Üí}
