   428‚Üífunction displayToolCallWithResult(
   429‚Üí  toolCall: any,
   430‚Üí  toolCallJson: any,
   431‚Üí  toolResultJson: any,
   432‚Üí  callTimestamp: string,
   433‚Üí  resultTimestamp: string
   434‚Üí) {
   435‚Üí  // Display the tool call header
   436‚Üí  process.stdout.write(`${callTimestamp}${formatConcise(toolCallJson)}\n`);
   437‚Üí
   438‚Üí  // Display the result
   439‚Üí  const toolResult = toolResultJson.message.content[0];
   440‚Üí  const isError = toolResult.is_error;
   441‚Üí  const resultIcon = isError ? '‚ùå' : '‚úÖ';
   442‚Üí  const resultColor = isError ? colors.red : colors.green;
   443‚Üí
   444‚Üí  process.stdout.write(
   445‚Üí    `  ${resultTimestamp}${resultIcon} ${resultColor}Tool Result${colors.reset}`
   446‚Üí  );
   447‚Üí
   448‚Üí  if (toolResult.content) {
   449‚Üí    const resultText =
   450‚Üí      typeof toolResult.content === 'string'
   451‚Üí        ? toolResult.content
   452‚Üí        : JSON.stringify(toolResult.content);
   453‚Üí    const lines = resultText.split('\n');
   454‚Üí    const chars = resultText.length;
   455‚Üí
   456‚Üí    process.stdout.write(` ${colors.dim}(${lines.length} lines, ${chars} chars)${colors.reset}`);
   457‚Üí
   458‚Üí    if (isError) {
   459‚Üí      process.stdout.write(` ${colors.red}ERROR${colors.reset}`);
   460‚Üí    }
   461‚Üí
   462‚Üí    // Show first few lines of result
   463‚Üí    const linesToShow = Math.min(3, lines.length);
   464‚Üí    for (let i = 0; i < linesToShow; i++) {
   465‚Üí      if (lines[i].trim()) {
   466‚Üí        const lineColor = i === 0 ? colors.reset : colors.dim;
   467‚Üí        process.stdout.write(`\n    ‚éø  ${lineColor}${lines[i]}${colors.reset}`);
   468‚Üí      }
   469‚Üí    }
   470‚Üí
   471‚Üí    if (lines.length > linesToShow) {
   472‚Üí      process.stdout.write(
   473‚Üí        `\n    ‚éø  ${colors.dim}... ${lines.length - linesToShow} more lines${colors.reset}`
   474‚Üí      );
   475‚Üí    }
   476‚Üí  }
   477‚Üí
   478‚Üí  process.stdout.write('\n\n');
   479‚Üí}
   480‚Üí
   481‚Üíif (import.meta.main) {
   482‚Üí  processStream().catch(console.error);
   483‚Üí}

