   162‚Üí        this.broadcastState();
   163‚Üí      }
   164‚Üí    } catch (error) {
   165‚Üí      this.state.error = error.message;
   166‚Üí      this.broadcast({
   167‚Üí        type: 'error',
   168‚Üí        error: `Failed to start agent: ${error.message}`,
   169‚Üí      });
   170‚Üí    }
   171‚Üí  }
