   189‚Üí        this.agentProcess.kill('SIGKILL');
   190‚Üí      }
   191‚Üí    }, 5000);
   192‚Üí  }
   193‚Üí
   194‚Üí  private async pauseAgent() {
   195‚Üí    if (this.state.status !== 'running' || !this.agentProcess) {
   196‚Üí      this.broadcast({
   197‚Üí        type: 'error',
