   208‚Üí
   209‚Üí  private async resumeAgent() {
   210‚Üí    if (this.state.status !== 'paused' || !this.agentProcess) {
   211‚Üí      this.broadcast({
   212‚Üí        type: 'error',
   213‚Üí        error: 'Agent is not paused',
   214‚Üí      });
