   252‚Üí      
   253‚Üí      const singleRun = spawn('bash', [scriptPath], {
   254‚Üí        cwd: join(__dirname, '..'),
   255‚Üí      });
   256‚Üí
   257‚Üí      singleRun.stdout?.on('data', (data) => {
   258‚Üí        this.broadcast({
   259‚Üí          type: 'single-output',
