     1‚Üí#!/usr/bin/env bun
     2‚Üí
     3‚Üíimport { WebSocketServer } from 'ws';
     4‚Üíimport { spawn, ChildProcess } from 'child_process';
     5‚Üíimport { existsSync, readFileSync, writeFileSync, unlinkSync } from 'fs';
     6‚Üíimport { join } from 'path';
     7‚Üí
     8‚Üíinterface AgentState {
     9‚Üí  status: 'idle' | 'running' | 'paused' | 'stopping';
    10‚Üí  pid: number | null;
    11‚Üí  startTime: Date | null;
    12‚Üí  iterations: number;
    13‚Üí  lastOutput: string;
    14‚Üí  error: string | null;
    15‚Üí}
    16‚Üí
    17‚Üíclass AgentControlServer {
    18‚Üí  private wss: WebSocketServer;
    19‚Üí  private agentProcess: ChildProcess | null = null;
    20‚Üí  private state: AgentState = {
    21‚Üí    status: 'idle',
    22‚Üí    pid: null,
    23‚Üí    startTime: null,
    24‚Üí    iterations: 0,
    25‚Üí    lastOutput: '',
    26‚Üí    error: null,
    27‚Üí  };
    28‚Üí  private clients: Set<any> = new Set();
    29‚Üí  private pidFile = join(__dirname, '.agent.pid');
    30‚Üí  private statusFile = join(__dirname, '../.agent/status.md');
    31‚Üí
    32‚Üí  constructor(port: number = 8080) {
    33‚Üí    this.wss = new WebSocketServer({ port });
    34‚Üí    this.setupWebSocket();
    35‚Üí    this.checkExistingProcess();
    36‚Üí    console.log(`Agent Control Server started on port ${port}`);
    37‚Üí  }
    38‚Üí
    39‚Üí  private setupWebSocket() {
    40‚Üí    this.wss.on('connection', (ws) => {
    41‚Üí      this.clients.add(ws);
    42‚Üí      console.log('New client connected');
    43‚Üí      
    44‚Üí      ws.send(JSON.stringify({
    45‚Üí        type: 'state',
    46‚Üí        data: this.state,
    47‚Üí      }));
    48‚Üí
    49‚Üí      ws.on('message', (message) => {
    50‚Üí        try {
    51‚Üí          const msg = JSON.parse(message.toString());
    52‚Üí          this.handleCommand(msg, ws);
    53‚Üí        } catch (error) {
    54‚Üí          ws.send(JSON.stringify({
    55‚Üí            type: 'error',
    56‚Üí            error: 'Invalid message format',
    57‚Üí          }));
    58‚Üí        }
    59‚Üí      });
    60‚Üí
    61‚Üí      ws.on('close', () => {
    62‚Üí        this.clients.delete(ws);
    63‚Üí        console.log('Client disconnected');
    64‚Üí      });
    65‚Üí    });
    66‚Üí  }
    67‚Üí
    68‚Üí  private async handleCommand(msg: any, ws: any) {
    69‚Üí    const { command, params } = msg;
    70‚Üí    
    71‚Üí    switch (command) {
    72‚Üí      case 'start':
    73‚Üí        await this.startAgent();
    74‚Üí        break;
    75‚Üí      case 'stop':
    76‚Üí        await this.stopAgent();
    77‚Üí        break;
    78‚Üí      case 'pause':
    79‚Üí        await this.pauseAgent();
    80‚Üí        break;
    81‚Üí      case 'resume':
    82‚Üí        await this.resumeAgent();
    83‚Üí        break;
    84‚Üí      case 'reset':
    85‚Üí        await this.resetAgent();
    86‚Üí        break;
    87‚Üí      case 'trigger':
    88‚Üí        await this.triggerSingleRun();
    89‚Üí        break;
    90‚Üí      case 'status':
    91‚Üí        this.broadcastState();
    92‚Üí        break;
    93‚Üí      case 'configure':
    94‚Üí        await this.configure(params);
    95‚Üí        break;
    96‚Üí      default:
    97‚Üí        ws.send(JSON.stringify({
    98‚Üí          type: 'error',
    99‚Üí          error: `Unknown command: ${command}`,
   100‚Üí        }));
   101‚Üí    }
   102‚Üí  }
   103‚Üí
   104‚Üí  private async startAgent() {
   105‚Üí    if (this.state.status === 'running') {
   106‚Üí      this.broadcast({
   107‚Üí        type: 'error',
   108‚Üí        error: 'Agent is already running',
   109‚Üí      });
   110‚Üí      return;
   111‚Üí    }
   112‚Üí
   113‚Üí    try {
   114‚Üí      const scriptPath = join(__dirname, 'ralph-enhanced.sh');
   115‚Üí      
   116‚Üí      this.agentProcess = spawn('bash', [scriptPath], {
   117‚Üí        cwd: join(__dirname, '..'),
   118‚Üí        env: { ...process.env, AGENT_CONTROL: 'true' },
   119‚Üí      });
   120‚Üí
   121‚Üí      if (this.agentProcess.pid) {
   122‚Üí        this.state.status = 'running';
   123‚Üí        this.state.pid = this.agentProcess.pid;
   124‚Üí        this.state.startTime = new Date();
   125‚Üí        this.state.error = null;
   126‚Üí        
   127‚Üí        writeFileSync(this.pidFile, this.agentProcess.pid.toString());
   128‚Üí        this.updateStatusFile('Agent started');
   129‚Üí
   130‚Üí        this.agentProcess.stdout?.on('data', (data) => {
   131‚Üí          const output = data.toString();
   132‚Üí          this.state.lastOutput = output;
   133‚Üí          this.state.iterations++;
   134‚Üí          this.broadcast({
   135‚Üí            type: 'output',
   136‚Üí            data: output,
   137‚Üí          });
   138‚Üí        });
   139‚Üí
   140‚Üí        this.agentProcess.stderr?.on('data', (data) => {
   141‚Üí          const error = data.toString();
   142‚Üí          this.state.error = error;
   143‚Üí          this.broadcast({
   144‚Üí            type: 'error',
   145‚Üí            error,
   146‚Üí          });
   147‚Üí        });
   148‚Üí
   149‚Üí        this.agentProcess.on('exit', (code) => {
   150‚Üí          this.state.status = 'idle';
   151‚Üí          this.state.pid = null;
   152‚Üí          if (existsSync(this.pidFile)) {
   153‚Üí            unlinkSync(this.pidFile);
   154‚Üí          }
   155‚Üí          this.updateStatusFile(`Agent stopped with code ${code}`);
   156‚Üí          this.broadcast({
   157‚Üí            type: 'stopped',
   158‚Üí            code,
   159‚Üí          });
   160‚Üí        });
   161‚Üí
   162‚Üí        this.broadcastState();
   163‚Üí      }
   164‚Üí    } catch (error) {
   165‚Üí      this.state.error = error.message;
   166‚Üí      this.broadcast({
   167‚Üí        type: 'error',
   168‚Üí        error: `Failed to start agent: ${error.message}`,
   169‚Üí      });
   170‚Üí    }
   171‚Üí  }
   172‚Üí
   173‚Üí  private async stopAgent() {
   174‚Üí    if (this.state.status !== 'running' || !this.agentProcess) {
   175‚Üí      this.broadcast({
   176‚Üí        type: 'error',
   177‚Üí        error: 'Agent is not running',
   178‚Üí      });
   179‚Üí      return;
   180‚Üí    }
   181‚Üí
   182‚Üí    this.state.status = 'stopping';
   183‚Üí    this.broadcastState();
   184‚Üí    
   185‚Üí    this.agentProcess.kill('SIGTERM');
