   188‚Üí      if (this.agentProcess && !this.agentProcess.killed) {
   189‚Üí        this.agentProcess.kill('SIGKILL');
   190‚Üí      }
   191‚Üí    }, 5000);
   192‚Üí  }
   193‚Üí
