     3→import { createInterface } from 'node:readline';
     4→
     5→const colors = {
     6→  reset: '\x1b[0m',
     7→  bright: '\x1b[1m',
     8→  dim: '\x1b[2m',
     9→  red: '\x1b[31m',
    10→  green: '\x1b[32m',
    11→  yellow: '\x1b[33m',
    12→  blue: '\x1b[34m',
    13→  magenta: '\x1b[35m',
    14→  cyan: '\x1b[36m',
    15→};
    16→
    17→function getTypeColor(type: string): string {
    18→  switch (type) {
    19→    case 'system':
    20→      return colors.magenta;
    21→    case 'user':
    22→      return colors.blue;
    23→    case 'assistant':
    24→      return colors.green;
    25→    case 'tool_use':
    26→      return colors.cyan;
    27→    case 'tool_result':
    28→      return colors.yellow;
    29→    case 'message':
    30→      return colors.dim;
    31→    case 'text':
    32→      return colors.reset;
    33→    default:
    34→      return colors.reset;
    35→  }
    36→}
    37→
    38→function _formatHeader(json: any, lineNumber: number): string {
    39→  const type = json.type || 'unknown';
    40→  const typeColor = getTypeColor(type);
    41→
    42→  let header = `${colors.dim}--- Line ${lineNumber} ${typeColor}[${type.toUpperCase()}]${colors.reset}`;
    43→
    44→  // Add context based on type
    45→  if (json.message?.role) {
    46→    header += ` ${colors.dim}(${json.message.role})${colors.reset}`;
    47→  }
    48→
    49→  if (json.message?.content?.[0]?.name) {
    50→    header += ` ${colors.cyan}${json.message.content[0].name}${colors.reset}`;
    51→  }
    52→
    53→  if (json.name) {
    54→    header += ` ${colors.cyan}${json.name}${colors.reset}`;
    55→  }
    56→
    57→  if (json.subtype) {
    58→    header += ` ${colors.dim}${json.subtype}${colors.reset}`;
    59→  }
    60→
    61→  return `${header} ${colors.dim}---${colors.reset}`;
    62→}
    63→
    64→function _colorizeJson(obj: any, indent = 0, path: string[] = []): string {
    65→  const spaces = '  '.repeat(indent);
    66→
    67→  if (obj === null) return `${colors.dim}null${colors.reset}`;
    68→  if (typeof obj === 'boolean') return `${colors.yellow}${obj}${colors.reset}`;
    69→  if (typeof obj === 'number') return `${colors.cyan}${obj}${colors.reset}`;
    70→  if (typeof obj === 'string') {
    71→    // Truncate very long strings
    72→    if (obj.length > 200) {
    73→      return `${colors.green}"${obj.substring(0, 197)}..."${colors.reset}`;
    74→    }
    75→    return `${colors.green}"${obj}"${colors.reset}`;
    76→  }
    77→
    78→  if (Array.isArray(obj)) {
    79→    if (obj.length === 0) return '[]';
    80→
    81→    // For content arrays, show summary
    82→    if (path.includes('content') && obj.length > 3) {
    83→      const summary = obj.slice(0, 2).map((item) => _colorizeJson(item, indent + 1, [...path]));
    84→      return `[\n${summary.join(',\n')},\n${spaces}  ${colors.dim}... ${obj.length - 2} more items${colors.reset}\n${spaces}]`;
    85→    }
    86→
    87→    const items = obj.map((item) => `${spaces}  ${_colorizeJson(item, indent + 1, [...path])}`);
    88→    return `[\n${items.join(',\n')}\n${spaces}]`;
    89→  }
    90→
    91→  if (typeof obj === 'object') {
    92→    const keys = Object.keys(obj);
    93→    if (keys.length === 0) return '{}';
    94→
    95→    // Show only key fields for deeply nested objects
    96→    const importantKeys = [
    97→      'type',
    98→      'role',
    99→      'name',
   100→      'id',
   101→      'input',
   102→      'output',
   103→      'content',
   104→      'text',
   105→      'subtype',
   106→      'session_id',
   107→    ];
   108→    const keysToShow = indent > 2 ? keys.filter((k) => importantKeys.includes(k)) : keys;
   109→
   110→    if (keysToShow.length === 0 && keys.length > 0) {
   111→      return `${colors.dim}{...${keys.length} keys}${colors.reset}`;
   112→    }
   113→
   114→    const items = keysToShow.map((key) => {
   115→      let coloredKey = `${colors.blue}"${key}"${colors.reset}`;
   116→
   117→      // Highlight important keys
   118→      if (['type', 'name', 'role'].includes(key)) {
   119→        coloredKey = `${colors.bright}${colors.blue}"${key}"${colors.reset}`;
   120→      }
   121→
   122→      const value = _colorizeJson(obj[key], indent + 1, [...path, key]);
   123→      return `${spaces}  ${coloredKey}: ${value}`;
   124→    });
   125→
   126→    if (keysToShow.length < keys.length) {
   127→      items.push(
   128→        `${spaces}  ${colors.dim}... ${keys.length - keysToShow.length} more keys${colors.reset}`
   129→      );
   130→    }
   131→
   132→    return `{\n${items.join(',\n')}\n${spaces}}`;
   133→  }
   134→
   135→  return String(obj);
   136→}
