   138→function formatTodoList(todos: any[]): string {
   139→  let output = `📋 ${colors.bright}${colors.cyan}Todo List Update${colors.reset}\n`;
   140→
   141→  const statusColors = {
   142→    completed: colors.dim + colors.green,
   143→    in_progress: colors.bright + colors.yellow,
   144→    pending: colors.reset,
   145→  };
   146→
   147→  const statusIcons = {
   148→    completed: '✅',
   149→    in_progress: '🔄',
   150→    pending: '⏸️',
   151→  };
   152→
   153→  const priorityColors = {
   154→    high: colors.red,
   155→    medium: colors.yellow,
   156→    low: colors.dim,
   157→  };
   158→
   159→  todos.forEach((todo, index) => {
   160→    const statusColor = statusColors[todo.status] || colors.reset;
   161→    const statusIcon = statusIcons[todo.status] || '❓';
   162→    const priorityColor = priorityColors[todo.priority] || colors.reset;
   163→    const checkbox = todo.status === 'completed' ? '☑️' : '☐';
   164→
   165→    output += `  ${checkbox} ${statusIcon} ${statusColor}${todo.content}${colors.reset}`;
   166→    output += ` ${priorityColor}[${todo.priority}]${colors.reset}`;
   167→
   168→    if (todo.status === 'in_progress') {
   169→      output += ` ${colors.bright}${colors.yellow}← ACTIVE${colors.reset}`;
   170→    }
   171→
   172→    output += '\n';
   173→  });
   174→
   175→  // Add summary stats
   176→  const completed = todos.filter((t) => t.status === 'completed').length;
   177→  const inProgress = todos.filter((t) => t.status === 'in_progress').length;
   178→  const pending = todos.filter((t) => t.status === 'pending').length;
   179→
   180→  output += `\n  ${colors.dim}📊 Progress: ${colors.green}${completed} completed${colors.reset}`;
   181→  output += `${colors.dim}, ${colors.yellow}${inProgress} active${colors.reset}`;
   182→  output += `${colors.dim}, ${colors.reset}${pending} pending${colors.reset}`;
   183→  output += `${colors.dim} (${Math.round((completed / todos.length) * 100)}% done)${colors.reset}`;
   184→
   185→  return output;
   186→}
   187→
   188→function formatConcise(json: any): string {
   189→  const type = json.type || 'unknown';
   190→  const typeColor = getTypeColor(type);
   191→
   192→  let output = `⏺ ${typeColor}${type.charAt(0).toUpperCase() + type.slice(1)}${colors.reset}`;
   193→
   194→  // Special handling for TodoWrite calls
   195→  if (type === 'assistant' && json.message?.content?.[0]?.name === 'TodoWrite') {
   196→    const toolInput = json.message.content[0].input;
   197→    if (toolInput?.todos && Array.isArray(toolInput.todos)) {
   198→      return formatTodoList(toolInput.todos);
   199→    }
   200→  }
   201→
   202→  // Add context based on type
   203→  if (type === 'assistant' && json.message?.content?.[0]?.name) {
   204→    const toolName = json.message.content[0].name;
   205→    const toolInput = json.message.content[0].input;
   206→
   207→    // Format tool name with key arguments
   208→    let toolDisplay = `${colors.cyan}${toolName}${colors.reset}`;
   209→
   210→    if (toolInput) {
   211→      const keyArgs = [];
   212→
   213→      // Extract the most important argument for each tool type
   214→      if (toolInput.file_path) keyArgs.push(toolInput.file_path);
   215→      else if (toolInput.path) keyArgs.push(toolInput.path);
   216→      else if (toolInput.pattern) keyArgs.push(`"${toolInput.pattern}"`);
   217→      else if (toolInput.command) keyArgs.push(toolInput.command);
   218→      else if (toolInput.cmd) keyArgs.push(toolInput.cmd);
   219→      else if (toolInput.query) keyArgs.push(`"${toolInput.query}"`);
   220→      else if (toolInput.description) keyArgs.push(toolInput.description);
   221→      else if (toolInput.prompt) keyArgs.push(`"${toolInput.prompt.substring(0, 30)}..."`);
   222→      else if (toolInput.url) keyArgs.push(toolInput.url);
   223→
   224→      if (keyArgs.length > 0) {
   225→        toolDisplay += `(${colors.green}${keyArgs[0]}${colors.reset})`;
   226→      }
   227→    }
   228→
   229→    output = `⏺ ${toolDisplay}`;
   230→
   231→    // Show additional arguments on next lines for complex tools
   232→    if (toolInput) {
   233→      const additionalArgs = [];
   234→
   235→      if (toolName === 'Bash' && toolInput.cwd) {
   236→        additionalArgs.push(`cwd: ${toolInput.cwd}`);
   237→      }
   238→      if (toolInput.limit) additionalArgs.push(`limit: ${toolInput.limit}`);
   239→      if (toolInput.offset) additionalArgs.push(`offset: ${toolInput.offset}`);
   240→      if (toolInput.include) additionalArgs.push(`include: ${toolInput.include}`);
   241→      if (toolInput.old_string && toolInput.new_string) {
   242→        additionalArgs.push(
   243→          `replace: "${toolInput.old_string.substring(0, 20)}..." → "${toolInput.new_string.substring(0, 20)}..."`
   244→        );
   245→      }
   246→      if (toolInput.timeout) additionalArgs.push(`timeout: ${toolInput.timeout}ms`);
   247→
   248→      if (additionalArgs.length > 0) {
   249→        output += `\n  ⎿  ${colors.dim}${additionalArgs.join(', ')}${colors.reset}`;
   250→      }
   251→    }
   252→  } else if (type === 'tool_result' && json.name) {
   253→    output += `(${colors.cyan}${json.name}${colors.reset})`;
   254→  } else if (type === 'user' && json.message?.content?.[0]) {
   255→    const content = json.message.content[0];
   256→    if (content.type === 'tool_result') {
   257→      // Override the type display for tool results
   258→      output = `⏺ ${colors.yellow}Tool Result${colors.reset}`;
   259→
   260→      // Show result summary and first 2 lines
   261→      if (content.content) {
   262→        const resultText =
   263→          typeof content.content === 'string' ? content.content : JSON.stringify(content.content);
   264→        const lines = resultText.split('\n');
   265→        const chars = resultText.length;
   266→        output += `\n  ⎿  ${colors.dim}${lines.length} lines, ${chars} chars${colors.reset}`;
   267→        if (content.is_error) {
   268→          output += ` ${colors.red}ERROR${colors.reset}`;
   269→        }
   270→
   271→        // Show first 2 lines of content
   272→        if (lines.length > 0 && lines[0].trim()) {
   273→          output += `\n  ⎿  ${colors.reset}${lines[0]}${colors.reset}`;
   274→        }
   275→        if (lines.length > 1 && lines[1].trim()) {
   276→          output += `\n      ${colors.dim}${lines[1]}${colors.reset}`;
   277→        }
   278→      }
   279→    } else if (content.text) {
   280→      const text = content.text.substring(0, 50);
   281→      output += `: ${colors.dim}${text}${text.length === 50 ? '...' : ''}${colors.reset}`;
   282→    }
   283→  } else if (type === 'system' && json.subtype) {
   284→    output += `(${colors.dim}${json.subtype}${colors.reset})`;
   285→  }
   286→
   287→  // Show assistant message content if it exists
   288→  if (type === 'assistant' && json.message?.content) {
   289→    const textContent = json.message.content.find((c) => c.type === 'text');
   290→    if (textContent?.text) {
   291→      const lines = textContent.text.split('\n').slice(0, 3); // Show first 3 lines
   292→      output += `\n  ⎿  ${colors.reset}${lines[0]}${colors.reset}`;
   293→      if (lines.length > 1) {
   294→        output += `\n      ${colors.dim}${lines[1]}${colors.reset}`;
   295→      }
   296→      if (lines.length > 2) {
   297→        output += `\n      ${colors.dim}${lines[2]}${colors.reset}`;
   298→      }
   299→      if (textContent.text.split('\n').length > 3) {
   300→        output += `\n      ${colors.dim}...${colors.reset}`;
   301→      }
   302→    }
   303→  }
   304→
   305→  // Add summary line
   306→  let summary = '';
   307→  if (json.message?.usage) {
   308→    const usage = json.message.usage;
   309→    summary = `${usage.input_tokens || 0}/${usage.output_tokens || 0} tokens`;
   310→  } else if (json.output && typeof json.output === 'string') {
   311→    summary = `${json.output.length} chars output`;
   312→  } else if (json.message?.content?.length) {
   313→    summary = `${json.message.content.length} content items`;
   314→  } else if (json.tools?.length) {
   315→    summary = `${json.tools.length} tools available`;
   316→  }
   317→
   318→  if (summary) {
   319→    output += `\n  ⎿  ${colors.dim}${summary}${colors.reset}`;
   320→  }
   321→
   322→  return output;
   323→}
