   325→async function processStream() {
   326→  const rl = createInterface({
   327→    input: process.stdin,
   328→    crlfDelay: Infinity,
   329→  });
   330→
   331→  const debugMode = process.argv.includes('--debug');
   332→  const toolCalls = new Map(); // Store tool calls by their ID
   333→  const pendingResults = new Map(); // Store results waiting for their tool calls
   334→  let lastLine = null; // Track the last line to detect final message
   335→  let isLastAssistantMessage = false;
   336→
   337→  rl.on('line', (line) => {
   338→    if (line.trim()) {
   339→      const timestamp = debugMode
   340→        ? `${colors.dim}[${new Date().toISOString()}]${colors.reset} `
   341→        : '';
   342→
   343→      try {
   344→        const json = JSON.parse(line);
   345→
   346→        // Check if this is a tool call
   347→        if (json.type === 'assistant' && json.message?.content?.[0]?.id) {
   348→          const toolCall = json.message.content[0];
   349→          const toolId = toolCall.id;
   350→
   351→          // Store the tool call
   352→          toolCalls.set(toolId, {
   353→            toolCall: json,
   354→            timestamp: timestamp,
   355→          });
   356→
   357→          // Check if we have a pending result for this tool call
   358→          if (pendingResults.has(toolId)) {
   359→            const result = pendingResults.get(toolId);
   360→            displayToolCallWithResult(
   361→              toolCall,
   362→              json,
   363→              result.toolResult,
   364→              result.timestamp,
   365→              timestamp
   366→            );
   367→            pendingResults.delete(toolId);
   368→          } else {
   369→            // Display the tool call and mark it as pending
   370→            process.stdout.write(`${timestamp + formatConcise(json)}\n`);
   371→            process.stdout.write(`${colors.dim}  ⎿  Waiting for result...${colors.reset}\n\n`);
   372→          }
   373→        }
   374→        // Check if this is a tool result
   375→        else if (json.type === 'user' && json.message?.content?.[0]?.type === 'tool_result') {
   376→          const toolResult = json.message.content[0];
   377→          const toolId = toolResult.tool_use_id;
   378→
   379→          if (toolCalls.has(toolId)) {
   380→            // We have the matching tool call, display them together
   381→            const stored = toolCalls.get(toolId);
   382→            displayToolCallWithResult(
   383→              stored.toolCall.message.content[0],
   384→              stored.toolCall,
   385→              json,
   386→              stored.timestamp,
   387→              timestamp
   388→            );
   389→            toolCalls.delete(toolId);
   390→          } else {
   391→            // Store the result and wait for the tool call
   392→            pendingResults.set(toolId, {
   393→              toolResult: json,
   394→              timestamp: timestamp,
   395→            });
   396→          }
   397→        }
   398→        // Check if this is the result message and display full content
   399→        else if (json.type === 'result' && json.result) {
   400→          process.stdout.write(`${timestamp + formatConcise(json)}\n\n`);
   401→          process.stdout.write(`${colors.bright}${colors.green}=== Final Result ===${colors.reset}\n\n`);
   402→          process.stdout.write(`${json.result}\n`);
   403→        }
   404→        // For all other message types, display normally
   405→        else {
   406→          process.stdout.write(`${timestamp + formatConcise(json)}\n\n`);
   407→        }
   408→
   409→        // Track if this might be the last assistant message
   410→        lastLine = json;
   411→        isLastAssistantMessage = json.type === 'assistant' && !json.message?.content?.[0]?.id;
   412→      } catch (_error) {
   413→        process.stdout.write(`${timestamp}${colors.red}⏺ Parse Error${colors.reset}\n`);
   414→        process.stdout.write(`  ⎿  ${colors.dim}${line.substring(0, 50)}...${colors.reset}\n\n`);
   415→      }
   416→    }
   417→  });
   418→
   419→  rl.on('close', () => {
   420→    // If the last message was an assistant message (not a tool call), display the full content
   421→    if (isLastAssistantMessage && lastLine?.message?.content?.[0]?.text) {
   422→      process.stdout.write(`\n${colors.bright}${colors.green}=== Final Assistant Message ===${colors.reset}\n\n`);
   423→      process.stdout.write(`${lastLine.message.content[0].text}\n`);
   424→    }
   425→  });
   426→}
   427→
   428→function displayToolCallWithResult(
   429→  toolCall: any,
   430→  toolCallJson: any,
   431→  toolResultJson: any,
   432→  callTimestamp: string,
   433→  resultTimestamp: string
   434→) {
   435→  // Display the tool call header
   436→  process.stdout.write(`${callTimestamp}${formatConcise(toolCallJson)}\n`);
   437→
   438→  // Display the result
   439→  const toolResult = toolResultJson.message.content[0];
   440→  const isError = toolResult.is_error;
   441→  const resultIcon = isError ? '❌' : '✅';
   442→  const resultColor = isError ? colors.red : colors.green;
   443→
   444→  process.stdout.write(
   445→    `  ${resultTimestamp}${resultIcon} ${resultColor}Tool Result${colors.reset}`
   446→  );
   447→
   448→  if (toolResult.content) {
   449→    const resultText =
   450→      typeof toolResult.content === 'string'
   451→        ? toolResult.content
   452→        : JSON.stringify(toolResult.content);
   453→    const lines = resultText.split('\n');
   454→    const chars = resultText.length;
   455→
   456→    process.stdout.write(` ${colors.dim}(${lines.length} lines, ${chars} chars)${colors.reset}`);
   457→
   458→    if (isError) {
   459→      process.stdout.write(` ${colors.red}ERROR${colors.reset}`);
   460→    }
   461→
   462→    // Show first few lines of result
   463→    const linesToShow = Math.min(3, lines.length);
   464→    for (let i = 0; i < linesToShow; i++) {
   465→      if (lines[i].trim()) {
   466→        const lineColor = i === 0 ? colors.reset : colors.dim;
   467→        process.stdout.write(`\n    ⎿  ${lineColor}${lines[i]}${colors.reset}`);
   468→      }
   469→    }
   470→
   471→    if (lines.length > linesToShow) {
   472→      process.stdout.write(
   473→        `\n    ⎿  ${colors.dim}... ${lines.length - linesToShow} more lines${colors.reset}`
   474→      );
   475→    }
   476→  }
   477→
   478→  process.stdout.write('\n\n');
   479→}
   480→
   481→if (import.meta.main) {
   482→  processStream().catch(console.error);
   483→}

