    16→
    17→class AgentControlServer {
    18→  private wss: WebSocketServer;
    19→  private agentProcess: ChildProcess | null = null;
    20→  private state: AgentState = {
    21→    status: 'idle',
    22→    pid: null,
    23→    startTime: null,
    24→    iterations: 0,
    25→    lastOutput: '',
    26→    error: null,
    27→  };
    28→  private clients: Set<any> = new Set();
    29→  private pidFile = join(__dirname, '.agent.pid');
    30→  private statusFile = join(__dirname, '../.agent/status.md');
    31→
    32→  constructor(port: number = 8080) {
    33→    this.wss = new WebSocketServer({ port });
    34→    this.setupWebSocket();
    35→    this.checkExistingProcess();
    36→    console.log(`Agent Control Server started on port ${port}`);
    37→  }
    38→
    39→  private setupWebSocket() {
    40→    this.wss.on('connection', (ws) => {
    41→      this.clients.add(ws);
    42→      console.log('New client connected');
    43→      
    44→      ws.send(JSON.stringify({
    45→        type: 'state',
    46→        data: this.state,
    47→      }));
    48→
    49→      ws.on('message', (message) => {
    50→        try {
    51→          const msg = JSON.parse(message.toString());
    52→          this.handleCommand(msg, ws);
    53→        } catch (error) {
    54→          ws.send(JSON.stringify({
    55→            type: 'error',
    56→            error: 'Invalid message format',
    57→          }));
    58→        }
    59→      });
    60→
    61→      ws.on('close', () => {
    62→        this.clients.delete(ws);
    63→        console.log('Client disconnected');
    64→      });
    65→    });
    66→  }
    67→
    68→  private async handleCommand(msg: any, ws: any) {
    69→    const { command, params } = msg;
    70→    
    71→    switch (command) {
    72→      case 'start':
    73→        await this.startAgent();
    74→        break;
    75→      case 'stop':
    76→        await this.stopAgent();
    77→        break;
    78→      case 'pause':
    79→        await this.pauseAgent();
    80→        break;
    81→      case 'resume':
    82→        await this.resumeAgent();
    83→        break;
    84→      case 'reset':
    85→        await this.resetAgent();
    86→        break;
    87→      case 'trigger':
    88→        await this.triggerSingleRun();
    89→        break;
    90→      case 'status':
    91→        this.broadcastState();
    92→        break;
    93→      case 'configure':
    94→        await this.configure(params);
    95→        break;
    96→      default:
    97→        ws.send(JSON.stringify({
    98→          type: 'error',
    99→          error: `Unknown command: ${command}`,
   100→        }));
   101→    }
   102→  }
   103→
   104→  private async startAgent() {
   105→    if (this.state.status === 'running') {
   106→      this.broadcast({
   107→        type: 'error',
   108→        error: 'Agent is already running',
   109→      });
   110→      return;
   111→    }
   112→
   113→    try {
   114→      const scriptPath = join(__dirname, 'ralph-enhanced.sh');
   115→      
   116→      this.agentProcess = spawn('bash', [scriptPath], {
   117→        cwd: join(__dirname, '..'),
   118→        env: { ...process.env, AGENT_CONTROL: 'true' },
   119→      });
   120→
   121→      if (this.agentProcess.pid) {
   122→        this.state.status = 'running';
   123→        this.state.pid = this.agentProcess.pid;
   124→        this.state.startTime = new Date();
   125→        this.state.error = null;
   126→        
   127→        writeFileSync(this.pidFile, this.agentProcess.pid.toString());
   128→        this.updateStatusFile('Agent started');
   129→
   130→        this.agentProcess.stdout?.on('data', (data) => {
   131→          const output = data.toString();
   132→          this.state.lastOutput = output;
   133→          this.state.iterations++;
   134→          this.broadcast({
   135→            type: 'output',
   136→            data: output,
   137→          });
   138→        });
   139→
   140→        this.agentProcess.stderr?.on('data', (data) => {
   141→          const error = data.toString();
   142→          this.state.error = error;
   143→          this.broadcast({
   144→            type: 'error',
   145→            error,
   146→          });
   147→        });
   148→
   149→        this.agentProcess.on('exit', (code) => {
   150→          this.state.status = 'idle';
   151→          this.state.pid = null;
   152→          if (existsSync(this.pidFile)) {
   153→            unlinkSync(this.pidFile);
   154→          }
   155→          this.updateStatusFile(`Agent stopped with code ${code}`);
   156→          this.broadcast({
   157→            type: 'stopped',
   158→            code,
   159→          });
   160→        });
   161→
   162→        this.broadcastState();
   163→      }
   164→    } catch (error) {
   165→      this.state.error = error.message;
   166→      this.broadcast({
   167→        type: 'error',
   168→        error: `Failed to start agent: ${error.message}`,
   169→      });
   170→    }
   171→  }
   172→
   173→  private async stopAgent() {
   174→    if (this.state.status !== 'running' || !this.agentProcess) {
   175→      this.broadcast({
   176→        type: 'error',
   177→        error: 'Agent is not running',
   178→      });
   179→      return;
   180→    }
   181→
   182→    this.state.status = 'stopping';
   183→    this.broadcastState();
   184→    
   185→    this.agentProcess.kill('SIGTERM');
   186→    
   187→    setTimeout(() => {
   188→      if (this.agentProcess && !this.agentProcess.killed) {
   189→        this.agentProcess.kill('SIGKILL');
   190→      }
   191→    }, 5000);
   192→  }
   193→
   194→  private async pauseAgent() {
   195→    if (this.state.status !== 'running' || !this.agentProcess) {
   196→      this.broadcast({
   197→        type: 'error',
   198→        error: 'Agent is not running',
   199→      });
   200→      return;
   201→    }
   202→
   203→    this.agentProcess.kill('SIGSTOP');
   204→    this.state.status = 'paused';
   205→    this.updateStatusFile('Agent paused');
   206→    this.broadcastState();
   207→  }
   208→
   209→  private async resumeAgent() {
   210→    if (this.state.status !== 'paused' || !this.agentProcess) {
   211→      this.broadcast({
   212→        type: 'error',
   213→        error: 'Agent is not paused',
   214→      });
   215→      return;
   216→    }
   217→
   218→    this.agentProcess.kill('SIGCONT');
   219→    this.state.status = 'running';
   220→    this.updateStatusFile('Agent resumed');
   221→    this.broadcastState();
   222→  }
   223→
   224→  private async resetAgent() {
   225→    await this.stopAgent();
   226→    
   227→    setTimeout(() => {
   228→      this.state = {
   229→        status: 'idle',
   230→        pid: null,
   231→        startTime: null,
   232→        iterations: 0,
   233→        lastOutput: '',
   234→        error: null,
   235→      };
   236→      this.updateStatusFile('Agent reset');
   237→      this.broadcastState();
   238→    }, 1000);
   239→  }
   240→
   241→  private async triggerSingleRun() {
   242→    if (this.state.status === 'running') {
   243→      this.broadcast({
   244→        type: 'error',
   245→        error: 'Agent is already running',
   246→      });
   247→      return;
   248→    }
   249→
   250→    try {
   251→      const scriptPath = join(__dirname, 'ralph-single.sh');
   252→      
   253→      const singleRun = spawn('bash', [scriptPath], {
   254→        cwd: join(__dirname, '..'),
   255→      });
   256→
   257→      singleRun.stdout?.on('data', (data) => {
   258→        this.broadcast({
   259→          type: 'single-output',
   260→          data: data.toString(),
   261→        });
   262→      });
   263→
   264→      singleRun.on('exit', (code) => {
   265→        this.broadcast({
   266→          type: 'single-complete',
   267→          code,
   268→        });
   269→      });
   270→    } catch (error) {
   271→      this.broadcast({
   272→        type: 'error',
   273→        error: `Failed to trigger single run: ${error.message}`,
   274→      });
   275→    }
   276→  }
   277→
   278→  private async configure(params: any) {
   279→    this.broadcast({
   280→      type: 'configured',
   281→      params,
   282→    });
   283→    this.updateStatusFile(`Configuration updated: ${JSON.stringify(params)}`);
   284→  }
   285→
   286→  private checkExistingProcess() {
   287→    if (existsSync(this.pidFile)) {
   288→      const pid = parseInt(readFileSync(this.pidFile, 'utf-8'));
   289→      try {
   290→        process.kill(pid, 0);
   291→        this.state.status = 'running';
   292→        this.state.pid = pid;
   293→        console.log(`Found existing agent process: ${pid}`);
   294→      } catch {
   295→        unlinkSync(this.pidFile);
   296→        console.log('Cleaned up stale PID file');
   297→      }
   298→    }
   299→  }
   300→
   301→  private updateStatusFile(message: string) {
   302→    const timestamp = new Date().toISOString();
   303→    const content = `# Agent Status
   304→
   305→**Last Updated:** ${timestamp}
   306→**Status:** ${this.state.status}
   307→**PID:** ${this.state.pid || 'N/A'}
   308→**Start Time:** ${this.state.startTime || 'N/A'}
   309→**Iterations:** ${this.state.iterations}
   310→**Last Message:** ${message}
   311→
   312→## Recent Output
   313→\`\`\`
   314→${this.state.lastOutput.slice(-500)}
   315→\`\`\`
   316→
   317→## Error Log
   318→${this.state.error ? `\`\`\`\n${this.state.error}\n\`\`\`` : 'No errors'}
   319→`;
   320→    
   321→    writeFileSync(this.statusFile, content);
   322→  }
   323→
   324→  private broadcast(message: any) {
   325→    const msg = JSON.stringify(message);
   326→    this.clients.forEach((client) => {
   327→      if (client.readyState === 1) {
   328→        client.send(msg);
   329→      }
   330→    });
   331→  }
   332→
   333→  private broadcastState() {
   334→    this.broadcast({
   335→      type: 'state',
   336→      data: this.state,
   337→    });
   338→  }
   339→}
