   428→function displayToolCallWithResult(
   429→  toolCall: any,
   430→  toolCallJson: any,
   431→  toolResultJson: any,
   432→  callTimestamp: string,
   433→  resultTimestamp: string
   434→) {
   435→  // Display the tool call header
   436→  process.stdout.write(`${callTimestamp}${formatConcise(toolCallJson)}\n`);
   437→
   438→  // Display the result
   439→  const toolResult = toolResultJson.message.content[0];
   440→  const isError = toolResult.is_error;
   441→  const resultIcon = isError ? '❌' : '✅';
   442→  const resultColor = isError ? colors.red : colors.green;
   443→
   444→  process.stdout.write(
   445→    `  ${resultTimestamp}${resultIcon} ${resultColor}Tool Result${colors.reset}`
   446→  );
   447→
   448→  if (toolResult.content) {
   449→    const resultText =
   450→      typeof toolResult.content === 'string'
   451→        ? toolResult.content
   452→        : JSON.stringify(toolResult.content);
   453→    const lines = resultText.split('\n');
   454→    const chars = resultText.length;
   455→
   456→    process.stdout.write(` ${colors.dim}(${lines.length} lines, ${chars} chars)${colors.reset}`);
   457→
   458→    if (isError) {
   459→      process.stdout.write(` ${colors.red}ERROR${colors.reset}`);
   460→    }
   461→
   462→    // Show first few lines of result
   463→    const linesToShow = Math.min(3, lines.length);
   464→    for (let i = 0; i < linesToShow; i++) {
   465→      if (lines[i].trim()) {
   466→        const lineColor = i === 0 ? colors.reset : colors.dim;
   467→        process.stdout.write(`\n    ⎿  ${lineColor}${lines[i]}${colors.reset}`);
   468→      }
   469→    }
   470→
   471→    if (lines.length > linesToShow) {
   472→      process.stdout.write(
   473→        `\n    ⎿  ${colors.dim}... ${lines.length - linesToShow} more lines${colors.reset}`
   474→      );
   475→    }
   476→  }
   477→
   478→  process.stdout.write('\n\n');
   479→}
   480→
   481→if (import.meta.main) {
   482→  processStream().catch(console.error);
   483→}

