'use client';

import React, { useState, useEffect, useRef } from 'react';
import { AgentControlButton, StatusIndicator } from '@/agent/button-interface';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Terminal, Activity, Clock, Hash, AlertCircle, CheckCircle2 } from 'lucide-react';

interface AgentState {
  status: 'idle' | 'running' | 'paused' | 'stopping';
  pid: number | null;
  startTime: Date | null;
  iterations: number;
  lastOutput: string;
  error: string | null;
}

interface LogEntry {
  timestamp: Date;
  type: 'output' | 'error' | 'info' | 'single';
  message: string;
}

export default function AgentControlPage() {
  const [connected, setConnected] = useState(false);
  const [agentState, setAgentState] = useState<AgentState>({
    status: 'idle',
    pid: null,
    startTime: null,
    iterations: 0,
    lastOutput: '',
    error: null,
  });
  const [logs, setLogs] = useState<LogEntry[]>([]);
  const wsRef = useRef<WebSocket | null>(null);
  const logsEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    connectWebSocket();
    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, []);

  useEffect(() => {
    logsEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [logs]);

  const connectWebSocket = () => {
    const ws = new WebSocket('ws://localhost:8080');

    ws.onopen = () => {
      setConnected(true);
      addLog('info', 'Connected to agent control server');
    };

    ws.onmessage = (event) => {
      try {
        const msg = JSON.parse(event.data);
        handleMessage(msg);
      } catch (error) {
        console.error('Failed to parse message:', error);
      }
    };

    ws.onclose = () => {
      setConnected(false);
      addLog('error', 'Disconnected from agent control server');
      setTimeout(connectWebSocket, 3000);
    };

    ws.onerror = (error) => {
      addLog('error', 'WebSocket error occurred');
    };

    wsRef.current = ws;
  };

  const handleMessage = (msg: any) => {
    switch (msg.type) {
      case 'state':
        setAgentState(msg.data);
        break;
      case 'output':
        addLog('output', msg.data);
        break;
      case 'error':
        addLog('error', msg.error);
        break;
      case 'single-output':
        addLog('single', msg.data);
        break;
      case 'stopped':
        addLog('info', `Agent stopped with code ${msg.code}`);
        break;
      case 'single-complete':
        addLog('info', `Single run completed with code ${msg.code}`);
        break;
      default:
        console.log('Unknown message type:', msg.type);
    }
  };

  const addLog = (type: LogEntry['type'], message: string) => {
    setLogs(prev => [...prev.slice(-100), {
      timestamp: new Date(),
      type,
      message,
    }]);
  };

  const sendCommand = (command: string) => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({ command }));
      addLog('info', `Sent command: ${command}`);
    }
  };

  const formatUptime = (startTime: Date | null) => {
    if (!startTime) return 'N/A';
    const diff = Date.now() - new Date(startTime).getTime();
    const hours = Math.floor(diff / 3600000);
    const minutes = Math.floor((diff % 3600000) / 60000);
    const seconds = Math.floor((diff % 60000) / 1000);
    return `${hours}h ${minutes}m ${seconds}s`;
  };

  const getLogColor = (type: LogEntry['type']) => {
    switch (type) {
      case 'error': return 'text-red-500';
      case 'info': return 'text-blue-500';
      case 'single': return 'text-purple-500';
      default: return 'text-gray-700';
    }
  };

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-2">Ralph Agent Control Center</h1>
        <p className="text-muted-foreground">
          Manage and monitor your autonomous coding agent
        </p>
      </div>

      {!connected && (
        <Alert className="mb-6 border-orange-500">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            Control server is offline. Please start it with: <code className="ml-2">bun agent/control-server.ts</code>
          </AlertDescription>
        </Alert>
      )}

      <div className="grid gap-6 md:grid-cols-2">
        <Card>
          <CardHeader>
            <CardTitle>Agent Controls</CardTitle>
            <CardDescription>Start, stop, and manage the agent lifecycle</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center justify-between mb-4">
              <span className="text-sm font-medium">Connection Status</span>
              <Badge variant={connected ? 'default' : 'destructive'}>
                {connected ? 'Connected' : 'Disconnected'}
              </Badge>
            </div>

            <AgentControlButton
              status={agentState.status}
              onStart={() => sendCommand('start')}
              onStop={() => sendCommand('stop')}
              onPause={() => sendCommand('pause')}
              onResume={() => sendCommand('resume')}
              onReset={() => sendCommand('reset')}
              onTrigger={() => sendCommand('trigger')}
            />
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Agent Status</CardTitle>
            <CardDescription>Current state and metrics</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <Activity className="w-4 h-4 text-muted-foreground" />
                  <span className="text-sm font-medium">Status</span>
                </div>
                <StatusIndicator status={agentState.status} />
              </div>

              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <Hash className="w-4 h-4 text-muted-foreground" />
                  <span className="text-sm font-medium">Process ID</span>
                </div>
                <Badge variant="outline">{agentState.pid || 'N/A'}</Badge>
              </div>

              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <Clock className="w-4 h-4 text-muted-foreground" />
                  <span className="text-sm font-medium">Uptime</span>
                </div>
                <span className="text-sm text-muted-foreground">
                  {formatUptime(agentState.startTime)}
                </span>
              </div>

              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <CheckCircle2 className="w-4 h-4 text-muted-foreground" />
                  <span className="text-sm font-medium">Iterations</span>
                </div>
                <Badge variant="secondary">{agentState.iterations}</Badge>
              </div>
            </div>

            {agentState.error && (
              <Alert className="mt-4 border-red-500">
                <AlertCircle className="h-4 w-4" />
                <AlertDescription className="text-red-600">
                  {agentState.error}
                </AlertDescription>
              </Alert>
            )}
          </CardContent>
        </Card>
      </div>

      <Card className="mt-6">
        <CardHeader>
          <CardTitle>Agent Logs</CardTitle>
          <CardDescription>Real-time output from the agent process</CardDescription>
        </CardHeader>
        <CardContent>
          <Tabs defaultValue="all" className="w-full">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="all">All Logs</TabsTrigger>
              <TabsTrigger value="output">Output Only</TabsTrigger>
              <TabsTrigger value="errors">Errors Only</TabsTrigger>
            </TabsList>

            <TabsContent value="all">
              <ScrollArea className="h-[400px] w-full rounded-md border p-4 bg-slate-950">
                <div className="space-y-1 font-mono text-xs">
                  {logs.map((log, index) => (
                    <div key={index} className={`flex gap-2 ${getLogColor(log.type)}`}>
                      <span className="text-slate-500">
                        [{log.timestamp.toLocaleTimeString()}]
                      </span>
                      <span className="flex-1 whitespace-pre-wrap break-all">
                        {log.message}
                      </span>
                    </div>
                  ))}
                  <div ref={logsEndRef} />
                </div>
              </ScrollArea>
            </TabsContent>

            <TabsContent value="output">
              <ScrollArea className="h-[400px] w-full rounded-md border p-4 bg-slate-950">
                <div className="space-y-1 font-mono text-xs">
                  {logs
                    .filter(log => log.type === 'output' || log.type === 'single')
                    .map((log, index) => (
                      <div key={index} className="text-gray-300">
                        <span className="text-slate-500">
                          [{log.timestamp.toLocaleTimeString()}]
                        </span>
                        <span className="ml-2 whitespace-pre-wrap break-all">
                          {log.message}
                        </span>
                      </div>
                    ))}
                </div>
              </ScrollArea>
            </TabsContent>

            <TabsContent value="errors">
              <ScrollArea className="h-[400px] w-full rounded-md border p-4 bg-slate-950">
                <div className="space-y-1 font-mono text-xs">
                  {logs
                    .filter(log => log.type === 'error')
                    .map((log, index) => (
                      <div key={index} className="text-red-500">
                        <span className="text-slate-500">
                          [{log.timestamp.toLocaleTimeString()}]
                        </span>
                        <span className="ml-2 whitespace-pre-wrap break-all">
                          {log.message}
                        </span>
                      </div>
                    ))}
                  {logs.filter(log => log.type === 'error').length === 0 && (
                    <div className="text-slate-500">No errors logged</div>
                  )}
                </div>
              </ScrollArea>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>

      <div className="mt-6 text-center text-sm text-muted-foreground">
        <Terminal className="inline-block w-4 h-4 mr-2" />
        Ralph Wiggum Autonomous Agent System v1.0
      </div>
    </div>
  );
}

