   241→  private async triggerSingleRun() {
   242→    if (this.state.status === 'running') {
   243→      this.broadcast({
   244→        type: 'error',
   245→        error: 'Agent is already running',
   246→      });
   247→      return;
   248→    }
   249→
   250→    try {
